import type { Gender, Base64Image } from './types';

// IMPORTANT: These are placeholder base64 strings.
// For a real application, use high-quality, consistently posed model images.
export const BASE_MODEL_IMAGE_FEMALE: Base64Image = {
  mimeType: 'image/jpeg',
  base64: '',
};

export const BASE_MODEL_IMAGE_MALE: Base64Image = {
  mimeType: 'image/jpeg',
  base64: '',
};

export const LOADING_MESSAGES = [
  'Warming up the AI stylist...',
  'Analyzing the clothing fabric and fit...',
  'Virtually tailoring the outfit...',
  'Checking the final look in the mirror...',
  'Almost ready for the runway!'
];

export const generatePrompt = (gender: Gender): string => {
  return `You are an expert AI fashion stylist. Your task is to perform a virtual try-on.
You are given two images:
1. A base image of a ${gender} model.
2. An image of an outfit.

Your goal is to realistically "dress" the model from the first image with the complete outfit from the second image.

Instructions:
- The output must be a single, photorealistic image of the ${gender} model wearing the new outfit.
- Preserve the model's original pose, face, hair, and body shape from the first image.
- The outfit from the second image should be accurately transferred to the model, including its style, color, texture, and fit.
- The background of the first image should be retained or replaced with a simple, neutral studio background if the original is too complex.
- Do not return text, only the final image.`;
};
